import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";
import { Form, Button, Col, Container, Row } from "react-bootstrap";
import { Link } from "react-router-dom";
import Header from "./Header";
import classes from "./Register.module.css";
import Config from "../../config/Config.json";

const Login = () => {
  const [inputs, setInputs] = useState({ email: "", password: "" });
  const [errors, setErrors] = useState({});
  const [backendErrors, setBackendErrors] = useState({
    show: false,
    message: "",
  });
  const dispatch = useDispatch();
  const history = useHistory();

  useEffect(() => {
    document.title = Config.TITLE.LOGIN;
  }, []);

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (validate()) {
      setBackendErrors({ show: false, message: "" });
      axios
        .post("http://localhost:8080/auth/login", inputs)
        .then((res) => {
          const token = res.data.token;
          dispatch({
            type: "SETAUTHTOKEN",
            data: token,
          });
          // Fetch user details after login
          axios
            .get("http://localhost:8080/auth/userinfo", {
              headers: { Authorization: `Bearer ${token}` },
            })
            .then((res) => {
              const user = res.data.user;
              // Store user details in local storage or Redux state
              localStorage.setItem("user", JSON.stringify(user));
              // Redirect to user dashboard
              history.push("/dashboard");
            })
            .catch((err) => console.log(err));
        })
        .catch((err) => {
          // Handle login errors
        });
    }
  };

  const validate = () => {
    // Validation logic
  };

  return (
    <React.Fragment>
      <Header />
      <Container className="mb-5 ">
        <h1 className="p-3 text-center rounded" style={{ color: "#2c49ed" }}>
          Login to Avsar
        </h1>
        <Row className="mb-5">
          <Col
            lg={5}
            md={6}
            sm={12}
            className={`${classes.formContainer} p-5 m-auto shadow-sm rounded-lg`}
          >
            {backendErrors.show && (
              <div className="login-error">{backendErrors.message}</div>
            )}
            <Form onSubmit={handleSubmit}>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>
                  Email <span style={{ color: "red" }}> *</span>
                </Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  placeholder="Enter email"
                  value={inputs.email}
                  onChange={handleChange}
                />
                <p style={{ color: "red" }}> {errors.email} </p>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>
                  Password <span style={{ color: "red" }}> *</span>
                </Form.Label>
                <Form.Control
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={inputs.password}
                  onChange={handleChange}
                />
                <p style={{ color: "red" }}> {errors.password} </p>
              </Form.Group>
              <div className="d-grid gap-2">
                <Button
                  variant="primary"
                  size="lg"
                  type="submit"
                  className="mt-4 mb-2"
                >
                  Log-In
                </Button>
              </div>
              <Col lg={5} md={6} sm={12}>
                <Link style={{ textDecoration: "none" }} to="/Reset">
                  {" "}
                  Forgot Password?{" "}
                </Link>
              </Col>
              <div>
                <Row>
                  <Col lg={5} md={6} sm={12} className="">
                    <Form.Label className="mt-5">
                      Don't have an account?{" "}
                    </Form.Label>
                  </Col>
                  <Col
                    lg={5}
                    md={6}
                    sm={12}
                    className=" mt-5  d-flex justify-content-center"
                  >
                    <Link className="" to="/Register">
                      <Button variant="success" size="lg">
                        {" "}
                        Sign-up{" "}
                      </Button>
                    </Link>
                  </Col>
                </Row>
              </div>
            </Form>
          </Col>
        </Row>
      </Container>
    </React.Fragment>
  );
};

export default Login;
